#!/bin/bash

if [[ ! `pwd` =~ "/web/node_modules" ]]; then
    echo "change directory to /web/node_modules"
    exit 1;
fi

find . -name "test"       -type d |xargs rm -rf
find . -name "example"    -type d |xargs rm -rf
find . -name "examples"   -type d |xargs rm -rf
find . -name "benchmark"  -type d |xargs rm -rf
find . -name "benchmarks" -type d |xargs rm -rf
find . -name "Makefile"           |xargs rm -f
find . -name ".npmignore"         |xargs rm -f
find . -name ".travis.yml"        |xargs rm -f
find . -name "*.md"               |xargs rm -f

cd sqlite3
npm uninstall -f node-pre-gyp
rm -f deps/sqlite-autoconf-*.tar.gz

echo "finished"
